package dataTypes;

public class Ifelse {
	
	int i = 100;
	
	public void display() {
		int j;
		if (i<110) {
			j=20;
		}
		else {
			j=20;
		}
		System.out.println(i+j);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Ifelse n = new Ifelse();
		n.display();

	}

}
